tinymce.PluginManager.add('fm', function(editor, url) {

    // Adds a menu item to the tools menu
    editor.addMenuItem('fm', {
        text: 'Файл с сервера',
        context: 'insert',
        image : url + '/img/icon.gif',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                file : '/filemanager?&embedded=1',
                width : '800',
                height : '450',
                inline: true,
                popup_css : false,                    
                title: 'Вставить файл с сервера',
            });
        }
    });
});
